import React, { useState, useRef } from 'react';
import { CloudArrowUpIcon, DocumentIcon, CheckCircleIcon } from '@heroicons/react/24/outline';
import storeOnBlockchain from "../contract/storeOnBlockchain.js";
import {
  ensureContractCode,
  ensureCorrectNetwork,
  ensureWalletConnected,
  getBrowserProvider,
  getReadOnlyContract,
} from "../contract/eth.js";
function Home() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadStatus, setUploadStatus] = useState(''); // '', 'uploading', 'hashing', 'done'
  const [hash, setHash] = useState('');
  const fileInputRef = useRef(null);

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFile(e.dataTransfer.files[0]);
      resetStatus();
    }
  };

  const handleFileSelect = (e) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
      resetStatus();
    }
  };

  const resetStatus = () => {
    setUploadStatus('');
    setHash('');
  };

  const handleUpload = async () => {
  if (!file) return;

  try {
    setUploading(true);
    setUploadStatus('uploading');

    const formData = new FormData();
    formData.append("file", file);

    const response = await fetch("http://localhost:5000/api/upload/handleupload", {
      method: "POST",
      body: formData,
    });

    if (!response.ok) {
      throw new Error('File upload failed');
    }

    const data = await response.json();
    if (!data?.result) {
      throw new Error('Upload response missing hash');
    }

    console.log(data)
    setUploadStatus('hashing');

    // File upload succeeded; blockchain write is a secondary step.
    try {
      await storeOnBlockchain(data.result);
    } catch (chainError) {
      console.error(chainError);
      alert(chainError?.message || 'Uploaded to IPFS, but failed to store hash on blockchain');
    }

    // small delay just for smooth UI transition
    setTimeout(() => {
      setUploadStatus('done');
      setHash(data.result);
      setUploading(false);
    }, 800);

  } catch (error) {
    console.error(error);
    setUploading(false);
    setUploadStatus('');
    alert("Upload failed");
  }
};

  const verifyFromBlockchain = async (hash) => {
    const provider = await getBrowserProvider();
    await ensureWalletConnected(provider);
    await ensureCorrectNetwork(provider);
    await ensureContractCode(provider);
    const contract = getReadOnlyContract(provider);
    const exists = await contract.verifyHash(hash);
    return exists;
  };

  const handleVerify = async () => {
    if (!hash) {
      alert('Hash not available');
      return;
    }

    try {
      const result = await verifyFromBlockchain(hash);

      if (result) {
        alert("Document Authentic (Blockchain Verified)");
      } else {
        alert("Not Found on Blockchain");
      }
    } catch (error) {
      alert(error?.message || 'Blockchain verification failed');
    }
  };

  const triggerFileInput = () => {
    fileInputRef.current.click();
  };
  // console.log("Contract Address:", contractAddress);
  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-xl p-8 max-w-md w-full transition-all duration-300 hover:shadow-2xl">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-2">Upload File</h1>
          <p className="text-gray-500">Securely store your documents in the vault</p>
        </div>

        {/* Drag & Drop Zone */}
        <div 
          className={`border-2 border-dashed rounded-xl p-8 mb-6 text-center cursor-pointer transition-colors duration-200 ${
            file ? 'border-blue-500 bg-blue-50' : 'border-gray-300 hover:border-blue-400 hover:bg-gray-50'
          }`}
          onDragOver={handleDragOver}
          onDrop={handleDrop}
          onClick={triggerFileInput}
        >
          <input 
            type="file" 
            className="hidden" 
            ref={fileInputRef} 
            onChange={handleFileSelect} 
          />
          
          {!file ? (
            <div className="flex flex-col items-center">
              <CloudArrowUpIcon className="h-12 w-12 text-gray-400 mb-3" />
              <p className="text-gray-600 font-medium">Drag & Drop or Click to Upload</p>
              <p className="text-xs text-gray-400 mt-1">Supported formats: PDF, DOCX, PNG, JPG</p>
            </div>
          ) : (
            <div className="flex flex-col items-center">
              <DocumentIcon className="h-12 w-12 text-blue-500 mb-3" />
              <p className="text-gray-800 font-medium truncate max-w-full">{file.name}</p>
              <p className="text-xs text-gray-500 mt-1">{(file.size / 1024 / 1024).toFixed(2)} MB</p>
            </div>
          )}
        </div>

        {/* Upload Button */}
        <button
          onClick={handleUpload}
          disabled={!file || uploading}
          className={`w-full py-3 px-4 rounded-lg text-white font-semibold shadow-md transition-all duration-200 transform hover:-translate-y-0.5 ${
            !file || uploading 
              ? 'bg-gray-400 cursor-not-allowed shadow-none hover:transform-none' 
              : 'bg-blue-600 hover:bg-blue-700 hover:shadow-lg'
          }`}
        >
          {uploading ? (
            <span className="flex items-center justify-center">
              <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Processing...
            </span>
          ) : 'Upload to Vault'}
        </button>

        {/* Status Indicators */}
        {uploadStatus && (
          <div className="mt-6 border-t border-gray-100 pt-6">
            <div className="space-y-3">
              <div className={`flex items-center space-x-3 text-sm ${
                uploadStatus === 'uploading' ? 'text-blue-600 font-medium' : 'text-gray-400'
              }`}>
                {uploadStatus === 'uploading' ? (
                   <div className="h-2 w-2 bg-blue-600 rounded-full animate-pulse"></div>
                ) : (
                   <div className="h-2 w-2 bg-gray-300 rounded-full"></div>
                )}
                <span>Uploading to IPFS...</span>
              </div>
              
              <div className={`flex items-center space-x-3 text-sm ${
                uploadStatus === 'hashing' ? 'text-purple-600 font-medium' : 
                uploadStatus === 'done' ? 'text-gray-500' : 'text-gray-400'
              }`}>
                 {uploadStatus === 'hashing' ? (
                   <div className="h-2 w-2 bg-purple-600 rounded-full animate-pulse"></div>
                ) : (
                   <div className="h-2 w-2 bg-gray-300 rounded-full"></div>
                )}
                <span>Generating Hash...</span>
              </div>

              {uploadStatus === 'done' && (
                <div className="bg-green-50 border border-green-100 rounded-lg p-3 flex items-start space-x-3 animate-fade-in-up">
                  <CheckCircleIcon className="h-5 w-5 text-green-500 flex-shrink-0 mt-0.5" />
                  <div className="overflow-hidden">
                    <p className="text-green-800 font-medium text-sm">Upload Complete</p>
                    <p className="text-green-600 text-xs mt-1 truncate font-mono bg-green-100 p-1 rounded select-all">
                      {hash}
                    </p>
                    {/* verify button shows once we have a hash */}
                    <button
                      onClick={handleVerify}
                      className="mt-2 w-full py-1 px-3 rounded-md text-white bg-indigo-600 hover:bg-indigo-700 text-xs"
                    >
                      Verify on Blockchain
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
