import React, { useMemo, useRef, useState } from "react";
import { CloudArrowUpIcon, DocumentIcon, CheckCircleIcon } from "@heroicons/react/24/outline";
import storeOnBlockchain from "../contract/storeOnBlockchain.js";
import {
  ensureContractCode,
  ensureCorrectNetwork,
  ensureWalletConnected,
  getBrowserProvider,
  getReadOnlyContract,
  getWriteContract,
} from "../contract/eth.js";

function Home() {
  const role = useMemo(() => localStorage.getItem("role"), []);
  const canUpload = role === "citizen" || role === "registrar";
  const canApprove = role === "registrar";
  const canVerify = role === "citizen" || role === "registrar" || role === "bank";

  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadStatus, setUploadStatus] = useState("");
  const [hash, setHash] = useState("");
  const [verifyInput, setVerifyInput] = useState("");
  const [verificationMessage, setVerificationMessage] = useState("");
  const [approving, setApproving] = useState(false);
  const fileInputRef = useRef(null);

  const resetStatus = () => {
    setUploadStatus("");
    setHash("");
    setVerificationMessage("");
    setVerifyInput("");
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFile(e.dataTransfer.files[0]);
      resetStatus();
    }
  };

  const handleFileSelect = (e) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
      resetStatus();
    }
  };

  const verifyFromBlockchain = async (value) => {
    const provider = await getBrowserProvider();
    await ensureWalletConnected(provider);
    await ensureCorrectNetwork(provider);
    await ensureContractCode(provider);
    const contract = getReadOnlyContract(provider);
    return contract.verifyHash(value);
  };

  const handleUpload = async () => {
    if (!canUpload) {
      alert("Only citizen and registrar can upload.");
      return;
    }
    if (!file) return;

    try {
      setUploading(true);
      setUploadStatus("uploading");
      setVerificationMessage("");

      const formData = new FormData();
      formData.append("file", file);

      const response = await fetch("http://localhost:5000/api/upload/handleupload", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("File upload failed");
      }

      const data = await response.json();
      if (!data?.result) {
        throw new Error("Upload response missing hash");
      }

      setUploadStatus("hashing");
      await storeOnBlockchain(data.result);

      setTimeout(() => {
        setUploadStatus("done");
        setHash(data.result);
        setVerifyInput(data.result);
        setUploading(false);
      }, 800);
    } catch (error) {
      console.error(error);
      setUploading(false);
      setUploadStatus("");
      alert(error?.message || "Upload failed");
    }
  };

  const handleVerify = async () => {
    if (!canVerify) {
      alert("You do not have permission to verify.");
      return;
    }

    const value = verifyInput || hash;
    if (!value) {
      alert("Enter or upload a hash first.");
      return;
    }

    try {
      const result = await verifyFromBlockchain(value);
      setVerificationMessage(result ? "Verified on blockchain" : "Not found on blockchain");
    } catch (error) {
      setVerificationMessage(error?.message || "Blockchain verification failed");
    }
  };

  const handleApprove = async () => {
    if (!canApprove) {
      alert("Only registrar can approve.");
      return;
    }
    const value = verifyInput || hash;
    if (!value) {
      alert("No hash available to approve.");
      return;
    }

    try {
      setApproving(true);
      const provider = await getBrowserProvider();
      await ensureWalletConnected(provider);
      await ensureCorrectNetwork(provider);
      await ensureContractCode(provider);
      const signer = await provider.getSigner();
      const contract = getWriteContract(signer);
      const tx = await contract.approveProperty(value);
      await tx.wait();
      setVerificationMessage("Property hash approved by registrar");
    } catch (error) {
      setVerificationMessage(error?.message || "Approval failed");
    } finally {
      setApproving(false);
    }
  };

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className="min-h-screen bg-slate-100 p-4">
      <div className="mx-auto w-full max-w-2xl rounded-xl bg-white p-8 shadow-xl">
        <div className="mb-6 text-center">
          <h1 className="text-3xl font-bold text-slate-800">Document Vault</h1>
          <p className="mt-2 text-sm text-slate-500">
            Logged in as <span className="font-semibold uppercase">{role || "guest"}</span>
          </p>
        </div>

        {canUpload && (
          <>
            <div
              className={`mb-6 cursor-pointer rounded-xl border-2 border-dashed p-8 text-center transition-colors ${
                file ? "border-blue-500 bg-blue-50" : "border-slate-300 hover:border-blue-400 hover:bg-slate-50"
              }`}
              onDragOver={handleDragOver}
              onDrop={handleDrop}
              onClick={triggerFileInput}
            >
              <input type="file" className="hidden" ref={fileInputRef} onChange={handleFileSelect} />

              {!file ? (
                <div className="flex flex-col items-center">
                  <CloudArrowUpIcon className="mb-3 h-12 w-12 text-slate-400" />
                  <p className="font-medium text-slate-700">Drag & Drop or Click to Upload</p>
                </div>
              ) : (
                <div className="flex flex-col items-center">
                  <DocumentIcon className="mb-3 h-12 w-12 text-blue-500" />
                  <p className="max-w-full truncate font-medium text-slate-800">{file.name}</p>
                </div>
              )}
            </div>

            <button
              onClick={handleUpload}
              disabled={!file || uploading}
              className={`w-full rounded-xl bg-gradient-to-r px-4 py-3 font-semibold text-white shadow-xl transition ${
                !file || uploading
                  ? "cursor-not-allowed from-slate-400 to-slate-500"
                  : "from-blue-600 to-indigo-600 hover:from-blue-500 hover:to-indigo-500"
              }`}
            >
              {uploading ? "Processing..." : "Upload Document"}
            </button>

            {uploadStatus === "done" && (
              <div className="mt-4 rounded-xl border border-emerald-200 bg-emerald-50 p-3">
                <div className="flex items-start gap-2">
                  <CheckCircleIcon className="mt-0.5 h-5 w-5 text-emerald-600" />
                  <div className="w-full">
                    <p className="text-sm font-medium text-emerald-800">Upload Complete</p>
                    <p className="mt-1 break-all rounded bg-emerald-100 p-2 font-mono text-xs text-emerald-700">
                      {hash}
                    </p>
                  </div>
                </div>
              </div>
            )}
          </>
        )}

        {canVerify && (
          <div className="mt-6 rounded-xl border border-slate-200 bg-slate-50 p-4">
            <label className="mb-2 block text-sm font-medium text-slate-700">Hash to verify</label>
            <input
              type="text"
              value={verifyInput}
              onChange={(e) => setVerifyInput(e.target.value.trim())}
              placeholder="Enter IPFS hash"
              className="w-full rounded-xl border border-slate-300 bg-white px-4 py-3 text-sm text-slate-800 outline-none focus:border-blue-500"
            />
            <div className="mt-3 flex flex-col gap-2 sm:flex-row">
              <button
                onClick={handleVerify}
                className="w-full rounded-xl bg-gradient-to-r from-emerald-600 to-teal-600 px-4 py-2.5 text-sm font-semibold text-white shadow-xl hover:from-emerald-500 hover:to-teal-500"
              >
                Verify Document
              </button>
              {canApprove && (
                <button
                  onClick={handleApprove}
                  disabled={approving}
                  className={`w-full rounded-xl bg-gradient-to-r px-4 py-2.5 text-sm font-semibold text-white shadow-xl ${
                    approving
                      ? "cursor-not-allowed from-amber-400 to-orange-400"
                      : "from-amber-600 to-orange-600 hover:from-amber-500 hover:to-orange-500"
                  }`}
                >
                  {approving ? "Approving..." : "Approve Document"}
                </button>
              )}
            </div>
            {verificationMessage && (
              <p className="mt-3 rounded-lg bg-white p-2 text-sm text-slate-700">{verificationMessage}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
