import React from "react";
import { Navigate, Route, Routes } from "react-router-dom";
import Login from "./pages/Login.js";
import Signup from "./pages/Signup.js";
import CitizenDashboard from "./pages/CitizenDashboard.jsx";
import RegistrarDashboard from "./pages/RegistrarDashboard.jsx";
import BankDashboard from "./pages/BankDashboard.jsx";
import ProtectedRoute from "./components/ProtectedRoute.js";
import PublicOnlyRoute from "./components/PublicOnlyRoute.js";
import { getHomePathForRole, getStoredRole } from "./utils/auth.js";

function RoleHomeRedirect() {
  const role = getStoredRole();
  return <Navigate to={getHomePathForRole(role)} replace />;
}

function App() {
  return (
    <Routes>
      <Route
        path="/login"
        element={
          <PublicOnlyRoute>
            <Login />
          </PublicOnlyRoute>
        }
      />
      <Route
        path="/signup"
        element={
          <PublicOnlyRoute>
            <Signup />
          </PublicOnlyRoute>
        }
      />
      <Route
        path="/citizen"
        element={
          <ProtectedRoute allowedRoles={["citizen"]}>
            <CitizenDashboard />
          </ProtectedRoute>
        }
      />
      <Route
        path="/registrar"
        element={
          <ProtectedRoute allowedRoles={["registrar"]}>
            <RegistrarDashboard />
          </ProtectedRoute>
        }
      />
      <Route
        path="/bank"
        element={
          <ProtectedRoute allowedRoles={["bank"]}>
            <BankDashboard />
          </ProtectedRoute>
        }
      />
      <Route path="/home" element={<RoleHomeRedirect />} />
      <Route path="/" element={<RoleHomeRedirect />} />
      <Route path="*" element={<Navigate to="/login" replace />} />
    </Routes>
  );
}

export default App;
