import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import AdminPanel from "../components/AdminPanel.js";
import {
  approvePropertyOnChain,
  getPropertyHistoryOnChain,
  getPropertyOwnerOnChain,
  getWalletRoleOnChain,
  grantRegistrarOnChain,
  isWalletRegistrarOnChain,
  propertyExistsOnChain,
  revokeRegistrarOnChain,
  verifyPropertyOnChain,
} from "../contract/propertyActions.js";

const API_BASE = "http://localhost:5000";
const ZERO_ADDRESS = "0x0000000000000000000000000000000000000000";

async function parseJsonSafe(response) {
  const contentType = response.headers.get("content-type") || "";
  if (!contentType.includes("application/json")) {
    const text = await response.text();
    throw new Error(
      `API did not return JSON. Response started with: ${text.slice(0, 40)}`
    );
  }
  return response.json();
}

function maskHash(hash) {
  const value = String(hash || "").trim();
  if (!value) return "-";
  if (value.length <= 14) return value;
  return `${value.slice(0, 8)}...${value.slice(-6)}`;
}

function maskValue(value) {
  const text = String(value || "").trim();
  if (!text) return "-";
  if (text.length <= 10) return text;
  return `${text.slice(0, 4)}...${text.slice(-4)}`;
}

function RegistrarDashboard() {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [actionMessage, setActionMessage] = useState("");
  const [processingHash, setProcessingHash] = useState("");
  const [walletInfo, setWalletInfo] = useState({
    wallet: "",
    adminAddress: "",
    isRegistrar: false,
    isAdmin: false,
  });
  const [granting, setGranting] = useState(false);
  const [revoking, setRevoking] = useState(false);
  const [grantAddress, setGrantAddress] = useState("");
  const [registrarUsers, setRegistrarUsers] = useState([]);
  const [loadingRegistrars, setLoadingRegistrars] = useState(false);
  const [historyByHash, setHistoryByHash] = useState({});
  const [loadingHistoryHash, setLoadingHistoryHash] = useState("");
  const [searchHash, setSearchHash] = useState("");
  const [searchingOwner, setSearchingOwner] = useState(false);
  const [ownerDetails, setOwnerDetails] = useState(null);
  const [ownerSearchMessage, setOwnerSearchMessage] = useState("");

  const logout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("role");
    navigate("/login", { replace: true });
  };

  const loadFiles = async () => {
    setLoading(true);
    try {
      let response = await fetch(`${API_BASE}/api/files?role=registrar`, {
        headers: { "x-role": "registrar" },
      });
      let data;

      try {
        data = await parseJsonSafe(response);
      } catch {
        // Backward-compatible fallback to existing upload listing endpoint.
        response = await fetch(`${API_BASE}/api/upload/all`);
        data = await parseJsonSafe(response);
      }

      if (!response.ok) {
        throw new Error(data?.message || "Failed to fetch files");
      }

      const rows = Array.isArray(data?.files) ? data.files : [];
      const withStatus = await Promise.all(
        rows.map(async (item) => {
          let existsOnChain = null;
          let verifiedOnChain = false;
          let currentOwnerWallet = item.currentOwnerWallet || null;
          let transferHistoryCount = null;

          try {
            existsOnChain = await propertyExistsOnChain(item.ipfshash);
          } catch {
            existsOnChain = null;
          }

          try {
            verifiedOnChain = await verifyPropertyOnChain(item.ipfshash);
          } catch {
            verifiedOnChain = false;
          }

          try {
            currentOwnerWallet = await getPropertyOwnerOnChain(item.ipfshash);
          } catch {
            // no-op
          }

          try {
            const history = await getPropertyHistoryOnChain(item.ipfshash);
            transferHistoryCount = Array.isArray(history)
              ? history.filter((record) => {
                  const from = String(record?.from || "").toLowerCase();
                  const to = String(record?.to || "").toLowerCase();
                  return from && to && from !== ZERO_ADDRESS && to !== ZERO_ADDRESS;
                }).length
              : 0;
          } catch {
            transferHistoryCount = null;
          }

          return {
            ...item,
            existsOnChain,
            verifiedOnChain: Boolean(verifiedOnChain),
            currentOwnerWallet,
            transferHistoryCount,
          };
        })
      );
      setFiles(withStatus);
    } catch (error) {
      setActionMessage(
        error?.message ||
          "Failed to load files. Ensure backend is running on http://localhost:5000."
      );
    } finally {
      setLoading(false);
    }
  };

  const loadWalletRole = async () => {
    try {
      const info = await getWalletRoleOnChain();
      setWalletInfo(info);
      setGrantAddress(info.wallet || "");
      if (!info.isRegistrar) {
        setActionMessage(
          `Connected wallet ${info.wallet} is not registrar on-chain. Approval will fail until added by admin.`
        );
      }
    } catch (error) {
      setActionMessage(error?.message || "Failed to read wallet role from blockchain.");
    }
  };

  const loadRegistrarUsers = async () => {
    try {
      setLoadingRegistrars(true);
      const response = await fetch(`${API_BASE}/api/auth/registrars`);
      const contentType = response.headers.get("content-type") || "";
      if (!contentType.includes("application/json")) {
        setRegistrarUsers([]);
        setActionMessage(
          "Registrar management endpoint is unavailable (non-JSON response). Restart backend and try again."
        );
        return;
      }
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data?.error || "Failed to load registrar users");
      }

      const rows = Array.isArray(data?.registrars) ? data.registrars : [];
      const withStatus = await Promise.all(
        rows.map(async (item) => {
          const wallet = String(item.walletAddress || "").trim();
          let isRegistrarOnChain = false;
          if (wallet) {
            try {
              isRegistrarOnChain = await isWalletRegistrarOnChain(wallet);
            } catch {
              isRegistrarOnChain = false;
            }
          }
          return { ...item, isRegistrarOnChain };
        })
      );
      setRegistrarUsers(withStatus);
    } catch (error) {
      setActionMessage(error?.message || "Failed to load registrar users.");
    } finally {
      setLoadingRegistrars(false);
    }
  };

  useEffect(() => {
    loadWalletRole();
    loadFiles();
    loadRegistrarUsers();
  }, []);

  const approve = async (hash) => {
    try {
      setProcessingHash(hash);
      setActionMessage("Approving property on blockchain...");
      await approvePropertyOnChain(hash);

      await fetch(`${API_BASE}/api/files/verify`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json", "x-role": "registrar" },
        body: JSON.stringify({
          hash,
          role: "registrar",
          verifiedBy: walletInfo.wallet || null,
        }),
      });

      setActionMessage("Property approved successfully.");
      setFiles((prev) =>
        prev.map((item) =>
          item.ipfshash === hash
            ? {
                ...item,
                existsOnChain: true,
                verifiedOnChain: true,
                verified: true,
                verifiedAt: new Date().toISOString(),
                verifiedBy: walletInfo.wallet || item.verifiedBy,
              }
            : item
        )
      );
    } catch (error) {
      const message = String(error?.message || "");
      if (message.includes("Property not found")) {
        setActionMessage(
          "Property hash not registered on blockchain. Citizen must upload/register hash first."
        );
      } else {
        setActionMessage(message || "Approval failed");
      }
    } finally {
      setProcessingHash("");
    }
  };

  const grantRegistrar = async (targetWalletAddress) => {
    try {
      const nextAddress = String(targetWalletAddress || grantAddress || "").trim();
      if (!nextAddress) {
        setActionMessage("Enter a wallet address to grant registrar role.");
        return;
      }
      setGranting(true);
      setActionMessage("Granting registrar role on-chain...");
      await grantRegistrarOnChain(nextAddress);
      if (nextAddress.toLowerCase() === walletInfo.wallet.toLowerCase()) {
        setWalletInfo((prev) => ({ ...prev, isRegistrar: true }));
      }
      await loadRegistrarUsers();
      setActionMessage(`Registrar role granted to ${nextAddress}.`);
    } catch (error) {
      setActionMessage(error?.message || "Failed to grant registrar role.");
    } finally {
      setGranting(false);
    }
  };

  const revokeRegistrar = async (targetWalletAddress) => {
    try {
      const nextAddress = String(targetWalletAddress || "").trim();
      if (!nextAddress) {
        setActionMessage("Enter a wallet address to revoke registrar role.");
        return;
      }
      if (nextAddress.toLowerCase() === String(walletInfo.adminAddress || "").toLowerCase()) {
        setActionMessage("Admin registrar role cannot be revoked.");
        return;
      }
      setRevoking(true);
      setActionMessage("Revoking registrar role on-chain...");
      await revokeRegistrarOnChain(nextAddress);
      if (nextAddress.toLowerCase() === String(walletInfo.wallet || "").toLowerCase()) {
        setWalletInfo((prev) => ({ ...prev, isRegistrar: false }));
      }
      await loadRegistrarUsers();
      setActionMessage(`Registrar role revoked for ${nextAddress}.`);
    } catch (error) {
      setActionMessage(error?.message || "Failed to revoke registrar role.");
    } finally {
      setRevoking(false);
    }
  };

  const loadHistory = async (hash) => {
    try {
      setLoadingHistoryHash(hash);
      const history = await getPropertyHistoryOnChain(hash);
      setHistoryByHash((prev) => ({ ...prev, [hash]: history }));
    } catch (error) {
      setActionMessage(error?.message || "Failed to fetch property history");
    } finally {
      setLoadingHistoryHash("");
    }
  };

  const searchOwnerByHash = async () => {
    const value = String(searchHash || "").trim();
    if (!value) {
      setOwnerSearchMessage("Enter property hash first.");
      setOwnerDetails(null);
      return;
    }

    try {
      setSearchingOwner(true);
      setOwnerSearchMessage("");
      setOwnerDetails(null);

      const response = await fetch(
        `${API_BASE}/api/property/${encodeURIComponent(value)}?role=registrar`,
        { headers: { "x-role": "registrar" } }
      );
      const data = await parseJsonSafe(response);
      if (!response.ok) {
        throw new Error(data?.message || "Failed to search property owner");
      }

      setOwnerDetails(data);
    } catch (error) {
      setOwnerSearchMessage(error?.message || "Failed to search owner by hash.");
    } finally {
      setSearchingOwner(false);
    }
  };

  const copyHash = async (hash) => {
    const value = String(hash || "").trim();
    if (!value) return;
    try {
      await navigator.clipboard.writeText(value);
      setActionMessage("Property hash copied.");
    } catch {
      setActionMessage("Copy failed. Please copy manually.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-100 via-slate-50 to-sky-100 p-4 md:p-6">
      <div className="mx-auto max-w-7xl space-y-6">
        <div className="rounded-2xl bg-white p-6 shadow-lg ring-1 ring-slate-200">
          <div className="flex flex-col gap-3 sm:flex-row sm:items-center sm:justify-between">
            <div>
              <h1 className="text-2xl font-bold tracking-tight text-slate-900">Registrar Dashboard</h1>
              <p className="text-sm text-slate-600">Review, verify, and track property records.</p>
            </div>
            <div className="flex items-center gap-2">
              <Link
                to="/user-lookup"
                className="rounded-xl bg-sky-600 px-4 py-2 text-sm font-semibold text-white shadow hover:bg-sky-700"
              >
                User Lookup
              </Link>
              <Link
                to="/explorer"
                className="rounded-xl bg-indigo-600 px-4 py-2 text-sm font-semibold text-white shadow hover:bg-indigo-700"
              >
                Explorer
              </Link>
              <button
                onClick={logout}
                className="rounded-xl bg-rose-600 px-4 py-2 text-sm font-semibold text-white shadow hover:bg-rose-700"
              >
                Logout
              </button>
            </div>
          </div>
        </div>

        <div className="grid gap-6 lg:grid-cols-2">


          <div className="rounded-2xl bg-white p-6 shadow-lg ring-1 ring-slate-200">
            <h2 className="text-lg font-semibold text-slate-900">Search By Property Hash</h2>
            <div className="mt-3 flex flex-col gap-2 sm:flex-row">
              <input
                type="text"
                value={searchHash}
                onChange={(e) => setSearchHash(e.target.value)}
                placeholder="Enter property hash"
                className="w-full rounded-xl border border-slate-300 px-3 py-2 text-xs outline-none focus:border-indigo-500"
              />
              <button
                onClick={searchOwnerByHash}
                disabled={searchingOwner}
                className={`rounded-xl px-4 py-2 text-xs font-semibold text-white ${
                  searchingOwner ? "cursor-not-allowed bg-slate-400" : "bg-indigo-600 hover:bg-indigo-700"
                }`}
              >
                {searchingOwner ? "Searching..." : "Search"}
              </button>
            </div>
            {ownerSearchMessage && (
              <p className="mt-3 rounded-lg border border-rose-200 bg-rose-50 px-3 py-2 text-xs text-rose-700">
                {ownerSearchMessage}
              </p>
            )}
            {ownerDetails && (
              <div className="mt-3 rounded-xl border border-slate-200 bg-slate-50 p-4">
                <p className="text-xs font-semibold uppercase tracking-wide text-slate-700">Owner Details</p>
                <p className="mt-2 break-all text-xs text-slate-700">
                  Wallet: <span className="font-mono">{ownerDetails.walletAddress || "-"}</span>
                </p>
                <p className="mt-1 text-xs text-slate-700">Name: {ownerDetails.name || "-"}</p>
                <p className="mt-1 text-xs text-slate-700">Role: {ownerDetails.role || "-"}</p>
                <p className="mt-1 text-xs text-slate-700">Aadhaar: {ownerDetails.aadhaar || "-"}</p>
              </div>
            )}
          </div>
        </div>

        <div className="rounded-2xl bg-white p-6 shadow-lg ring-1 ring-slate-200">
          <h2 className="text-lg font-semibold text-slate-900">Property Queue</h2>
          <div className="mt-3 rounded-xl border border-indigo-200 bg-indigo-50 p-3 text-xs text-indigo-900">
            <p className="font-semibold">Registrar Workflow</p>
            <p className="mt-1">
              1) If status is <span className="font-semibold">Missing On-Chain</span>, property hash is not
              registered yet.
            </p>
            <p className="mt-1">
              2) Only after on-chain registration, registrar can approve verification.
            </p>
            <p className="mt-1">
              3) Use <span className="font-semibold">View History</span> to review transfer ownership trail.
            </p>
          </div>
          {actionMessage && (
            <p className="mt-3 rounded-lg border border-sky-200 bg-sky-50 px-3 py-2 text-sm text-sky-800">
              {actionMessage}
            </p>
          )}
          {loading ? (
            <p className="mt-4 text-sm text-slate-500">Loading documents...</p>
          ) : files.length === 0 ? (
            <p className="mt-4 text-sm text-slate-500">No uploaded files found.</p>
          ) : (
            <div className="mt-4 overflow-x-auto rounded-xl border border-slate-200">
              <table className="min-w-full divide-y divide-slate-200">
                <thead>
                  <tr className="bg-slate-50 text-left text-xs uppercase tracking-wide text-slate-600">
                    <th className="px-3 py-2">File</th>
                    <th className="px-3 py-2">Owner ID</th>
                    <th className="px-3 py-2">Hash</th>
                    <th className="px-3 py-2">Current Owner</th>
                    <th className="px-3 py-2">Flow</th>
                    <th className="px-3 py-2">Status</th>
                    <th className="px-3 py-2">Action</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-slate-100 bg-white">
                  {files.map((item) => (
                    <React.Fragment key={item._id}>
                    <tr className="hover:bg-slate-50">
                      <td className="px-3 py-2 text-sm text-slate-800">{item.filename}</td>
                      <td className="px-3 py-2">
                        <button
                          type="button"
                          onClick={() => copyHash(item.userID)}
                          title={item.userID}
                          className="text-sm text-slate-700 underline decoration-dotted underline-offset-2 hover:text-indigo-700"
                        >
                          {maskValue(item.userID)}
                        </button>
                      </td>
                      <td className="px-3 py-2">
                        <button
                          type="button"
                          onClick={() => copyHash(item.ipfshash)}
                          title={item.ipfshash}
                          className="font-mono text-xs text-slate-700 underline decoration-dotted underline-offset-2 hover:text-indigo-700"
                        >
                          {maskHash(item.ipfshash)}
                        </button>
                      </td>
                      <td className="px-3 py-2">
                        <button
                          type="button"
                          onClick={() => copyHash(item.currentOwnerWallet)}
                          title={item.currentOwnerWallet || "-"}
                          className="font-mono text-xs text-slate-700 underline decoration-dotted underline-offset-2 hover:text-indigo-700"
                        >
                          {maskValue(item.currentOwnerWallet)}
                        </button>
                      </td>
                      <td className="px-3 py-2 text-xs text-slate-700">
                        {item.existsOnChain === false
                          ? "Registration Pending"
                          : Number(item.transferHistoryCount || 0) > 0
                          ? "Transferred Property"
                          : "New Registration"}
                      </td>
                      <td className="px-3 py-2 text-sm">
                        <span
                          className={
                            item.verified || item.verifiedOnChain
                              ? "text-emerald-700 font-medium"
                              : item.existsOnChain === false
                              ? "text-rose-700 font-medium"
                              : item.existsOnChain
                              ? "text-amber-700 font-medium"
                              : "text-amber-700 font-medium"
                          }
                        >
                          {item.verified || item.verifiedOnChain
                            ? "Verified"
                            : item.existsOnChain === false
                            ? "Not Registered"
                            : "Pending Approval"}
                        </span>
                      </td>
                      <td className="px-3 py-2">
                        <div className="flex flex-col gap-2">
                          <a
                            href={`https://gateway.pinata.cloud/ipfs/${item.ipfshash}`}
                            target="_blank"
                            rel="noreferrer"
                            className="rounded-lg bg-indigo-600 px-3 py-1.5 text-center text-xs font-semibold text-white hover:bg-indigo-700"
                          >
                            View Document
                          </a>
                          <button
                            onClick={() => approve(item.ipfshash)}
                            disabled={
                              item.existsOnChain === false ||
                              item.verified ||
                              item.verifiedOnChain ||
                              processingHash === item.ipfshash ||
                              !walletInfo.isRegistrar
                            }
                            className={`rounded-lg px-3 py-1.5 text-xs font-semibold text-white ${
                              item.existsOnChain === false ||
                              item.verified ||
                              item.verifiedOnChain ||
                              processingHash === item.ipfshash ||
                              !walletInfo.isRegistrar
                                ? "cursor-not-allowed bg-slate-400"
                                : "bg-amber-600 hover:bg-amber-700"
                            }`}
                            title={item.existsOnChain === false ? "Hash is missing on blockchain" : ""}
                          >
                            {item.existsOnChain === false
                              ? "Missing On-Chain"
                              : processingHash === item.ipfshash
                              ? "Approving..."
                              : "Approve"}
                          </button>
                          <button
                            onClick={() => loadHistory(item.ipfshash)}
                            disabled={loadingHistoryHash === item.ipfshash}
                            className={`rounded-lg px-3 py-1.5 text-xs font-semibold text-white ${
                              loadingHistoryHash === item.ipfshash
                                ? "cursor-not-allowed bg-slate-400"
                                : "bg-sky-600 hover:bg-sky-700"
                            }`}
                          >
                            {loadingHistoryHash === item.ipfshash ? "Loading..." : "View History"}
                          </button>
                        </div>
                      </td>
                    </tr>
                    {historyByHash[item.ipfshash] && (
                      <tr>
                        <td colSpan={7} className="bg-slate-50 px-3 py-2">
                          <p className="text-xs font-semibold text-slate-700">Transfer History</p>
                          {historyByHash[item.ipfshash].length === 0 ? (
                            <p className="text-xs text-slate-500">No transfer records.</p>
                          ) : (
                            <div className="mt-1 space-y-1">
                              {historyByHash[item.ipfshash].map((record, idx) => (
                                <p key={`${item.ipfshash}-${idx}`} className="break-all font-mono text-[11px] text-slate-600">
                                  {record.from} -> {record.to} at{" "}
                                  {record.timestamp ? new Date(record.timestamp * 1000).toLocaleString() : "-"}
                                </p>
                              ))}
                            </div>
                          )}
                        </td>
                      </tr>
                    )}
                    </React.Fragment>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default RegistrarDashboard;
