import React, { useState } from 'react';
import Home from './components/Home.js';
import FilesPage from './components/FilesPage.js';

function App() {
  const [activePage, setActivePage] = useState('upload');

  return (
    <div className="min-h-screen bg-slate-100">
      <div className="mx-auto flex max-w-5xl items-center justify-between px-4 pt-6">
        <h1 className="text-xl font-bold text-slate-800">eVault</h1>
        <div className="rounded-xl bg-white p-1 shadow-sm">
          <button
            onClick={() => setActivePage('upload')}
            className={`rounded-lg px-4 py-2 text-sm font-medium transition-colors ${
              activePage === 'upload' ? 'bg-blue-600 text-white' : 'text-slate-600 hover:bg-slate-100'
            }`}
          >
            Upload
          </button>
          <button
            onClick={() => setActivePage('files')}
            className={`rounded-lg px-4 py-2 text-sm font-medium transition-colors ${
              activePage === 'files' ? 'bg-blue-600 text-white' : 'text-slate-600 hover:bg-slate-100'
            }`}
          >
            All Files
          </button>
        </div>
      </div>

      <div className="pt-2">
        {activePage === 'upload' ? <Home /> : <FilesPage />}
      </div>
    </div>
  );
}

export default App;
